import React, {useState, useEffect, useRef} from "react";
import axios from 'axios'


function App(){

const apiKey="39b03ffb50ffbb93a24dd200557d9566";
const [city, setCity] =useState({ hits: [] });
const [selected, setSelected] = useState('')
const selectRef = useRef(null);


const getWeatherData= async() =>{
    await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${selected}&appid=${apiKey}`) 
    .then(response => response.json())
    .then(data => console.log("data = ",data));
}
 // get data from datajson 
 // eslint-disable-next-line react-hooks/exhaustive-deps
 useEffect( async () => {
  const result =  await axios('./data.json',);

  setCity(result.data);
  console.log("city = ",city.cities)


},[city])


const handleSelect =()=>{/*
  setSelected(selectRef.current.value )
  console.log(selected)
  */

}

return(
  <>
  <h1>welcome</h1>
  <label htmlFor="city">Choose a city : </label>
  <select name="city" id="city" ref={selectRef} onChange={handleSelect()}>
    
      {city.cities.map((item,index) =>{
       return <option value="city" key={index}>{ item } </option>
      })}
    
  </select>
  </>
);

}
export default App;
