import React, {useState, useEffect, useRef} from "react";
//import axios from 'axios'
import Data from './data.json'

function App(){

const apiKey="39b03ffb50ffbb93a24dd200557d9566";
const [city, setCity] =useState('');
const [weatherData, setWeatherData]= useState([{}])


const getWeatherData= async() =>{
    await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`) 
    .then(response => response.json())
    .then(data => {
      setWeatherData(data)
      console.log("weatherData = ",weatherData)});

}


const handleSelect =()=>{
  getWeatherData();
 
  console.log('city = ', city)
  return( <div className="weather-data">
  <p className="city">{weatherData.name}</p>
  <p className="temp">{Math.round(weatherData.main.temp)} F</p>
  <p className="weather">{weatherData.weather[0].main}</p>


</div>)

}

return(
  <>
  <h1>welcome</h1>
  <label htmlFor="city">Choose a city : </label>
  <select name="city" id="city"  onChange={(e)=> setCity(e.target.value)}>
  
  {Data.cities.map((item,index) =>{
       return <option value={item} key={index}>{ item } </option>
      })}
  </select>
  <button onClick={handleSelect}>show weather</button>
 
  </>
);

}
export default App;
