import React, {useState, useEffect, useRef} from "react";
//import axios from 'axios'
import Data from './data.json'

function App(){

const apiKey="39b03ffb50ffbb93a24dd200557d9566";
const [city, setCity] =useState('');


const getWeatherData= async() =>{
    await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`) 
    .then(response => response.json())
    .then(data => console.log("data = ",data));
}


const handleSelect =()=>{
  let val =document.getElementById('city').value
  setCity(val)
  console.log('city = ', city)
  

}

return(
  <>
  <h1>welcome</h1>
  <label htmlFor="city">Choose a city : </label>
  <select name="city" id="city"  onClick={handleSelect}>
  
  {Data.cities.map((item,index) =>{
       return <option value={item} key={index}>{ item } </option>
      })}
  </select>
  </>
);

}
export default App;
